import './App.css';
import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';

import { Form, useFormik } from "formik";
import { useRef } from 'react';
import { useEffect } from 'react';
import Box from '@mui/material/Box';
import { Button } from '@mui/material';
import Fade from '@mui/material/Fade';
import OutlinedCard from './cardComp'
// import SendIcon from '@mui/icons-material/Send';
import TextField from '@mui/material/TextField';
import send_icon from './assets/sent.png'
import { useState } from 'react';

function App() {
  const txt= useRef(null)
  const [json,setJson] = useState({})
  const [right,setRight] = useState("")
  const [left,setLeft] = useState("")
  const [mid,setMid] = useState("")
  const [submit, setSubmit] = useState(false);
  const formik = useFormik({
    initialValues: {
      p: '',
    },
    onSubmit: (values) => {
      fetch("http://localhost:5000/", {
        method: "GET",
        body: JSON.stringify({topic : values.p}),
        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then((data) => {
          console.log(data);
        });
    },
  });
  const show = submit

  return (

    <div className="App">
      <header className="App-header">
        <p id='header-title'>Controversy.io</p>
      </header>
      <div className='main-container'>
        <h1 id="controversy-title">Co:ntroversy</h1>
        <h3 id="sub-title">Access all sides of the argument, delivered by AI</h3>
         <Fade in={submit === false} timeout={2000}>
         <form className="topic-form" action="/action_page.php">
         <Box>
         <TextField ref={txt} id="outlined-basic"  variant="standard" sx={{bgcolor:"white", mx:3, width: '20vw'}} />   
         <Button variant="contained" size="small"  onClick={()=>{setSubmit(true); setTimeout(()=>3000)}} disabled={!setSubmit}> submit 
         </Button>
         </Box>
         </form> 
         </Fade>
      {/* <form onSubmit={formik.handleSubmit}>
       <input
         id="p"
         name="p"
         type="text"
         onChange={formik.handleChange}
         value={formik.values.p}
       />
       <button type="submit">Submit</button>
       </form>
        </Box> } */}
      </div>
      <Fade in={show === true} timeout={4000}>
       <Box>
        <Box sx = {{display: 'flex', mt : '5vh', justifyContent: 'center', alignContent: 'center'}}>
            <OutlinedCard name ="Positive Argument" data = "Left-wing critics of capitalism argue that it leads to inequality and concentrates wealth in the hands of a small elite, while exploiting workers and neglecting the needs of marginalized communities. They argue for a more equitable economic system, such as socialism, which prioritizes the needs of all members of society over the profits of a few. They also call for stronger regulations and protections for workers and the environment, as well as increased government intervention to redistribute wealth and address social issues."></OutlinedCard>
            <OutlinedCard name ="Neutral Argument" data = "Abortion is a complex and controversial issue. Different people have different perspectives on the matter. Some believe that a woman should have the right to make decisions about her own body and reproductive health, while others believe that abortion is morally wrong and that the unborn fetus has a right to life. It is important to consider the various moral, legal, and medical aspects of the issue, as well as the impact that it has on individuals and society as a whole. Ultimately, the decision about whether or not to have an abortion is a personal one that should be made after careful consideration and with the guidance of trusted individuals and professionals." ></OutlinedCard>
            <OutlinedCard name ="Negative Argument" data = "Abortion is a deeply divisive issue that has been at the center of political and social debate for decades. From a right-wing perspective, the belief is that the unborn child has a right to life and that abortion is morally wrong. It is seen as taking the life of an innocent human being and should be prohibited except in cases where the mother's life is in danger. Additionally, many right-wing individuals and organizations argue that alternative options, such as adoption, should be more widely available and promoted as a viable alternative to abortion." ></OutlinedCard>
        </Box>
        <Button sx = {{mt:'1.5vh', mb: '1.5vh'}} variant="contained" size="medium" onClick={()=>setSubmit(false)}> Try Again 
        </Button>
        </Box>
      </Fade>

    </div>

  )
}

export default App;
